import { Component } from '@angular/core';
import {HttpClient, HttpHeaders} from '@angular/common/http';
import { GameModel } from '../Model/GameModel';

@Component({
  selector: 'app-games',
  templateUrl: './games.component.html',
  styleUrls: ['./games.component.css']
})


export class GamesComponent {

  Games:any;

  constructor( private http:HttpClient){};
  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
  };

  ngOnInit() {
    this.http.get('https://localhost:7207/games').subscribe(data => this.Games = data);
  }

  addGame(game:any){
    var gameInfo = new GameModel(game.name, game.genre, game.price, game.releaseDate);
    return this.http.post('https://localhost:7207/games', gameInfo).subscribe(data => {
      data = gameInfo;
  });
  }

  deleteGame(game:any){
    return this.http.delete('https://localhost:7207/games/' + game.id).subscribe(data => {
      data = game;
  });
  }

  updateGame(game:any){
    return this.http.put('https://localhost:7207/games/' + game.id, game).subscribe(data => {
      data = game;
  });
  }

  getGames(){
    this.http.get('https://localhost:7207/games').subscribe(data => this.Games = data);
    //return this.http.get('https://localhost:7207/games');
    return this.Games;
  }

  getGame(id:any){
    return this.http.get('https://localhost:7207/games' + id);
  }

}

<div class="body">

<form #game = ngForm method="post" (ngSubmit)="addGame(game.value)">
    <label> Name </label>
    <input type = "text" name = "name" ngModel>
  <br>
    <label> Genre </label>
    <input type = "text" name = "genre" ngModel>
  <br>
    <label> Price  </label>
    <input type = "text" name = "price" ngModel>
  <br>
    <label> Release Date  </label>
    <input type="date" name = "releasedate" ngModel>
  <br>
  <button type="submit">
    Post
  </button>
</form>


<form #deletion = ngForm (ngSubmit)="deleteGame(deletion.value)">
  <label> ID </label>
  <input type = "text" name = "id" ngModel>
<button type="submit">
  Delete
</button>
</form>


<form #update = ngForm (ngSubmit)="updateGame(update.value)">
  <label> ID </label>
  <input type = "text" name = "id" ngModel>
<br>
  <label> Name </label>
  <input type = "text" name = "name" ngModel>
<br>
  <label> Genre </label>
  <input type = "text" name = "genre" ngModel>
<br>
  <label> Price  </label>
  <input type = "text" name = "price" ngModel>
<br>
  <label> Release Date  </label>
  <input type="date" name = "releasedate" ngModel>
<br>
<button type="submit">
  Put
</button>
</form>

<table>
<thead>
  <tr>
    Name
  </tr>
  <tr>
    Genre
  </tr>
  <tr>
    Price
  </tr>
  <tr>
    Release Date
  </tr>
</thead>
<tbody>
  <tr *ngFor="let item of Games">
    <th>
      {{item.id}}
    </th>
  </tr>
</tbody>
</table>

</div>
