import { Component } from '@angular/core';
import {HttpClient, HttpHeaders} from '@angular/common/http';
import { GameModel } from '../Model/GameModel';

@Component({
  selector: 'app-games',
  templateUrl: './games.component.html',
  styleUrls: ['./games.component.css']
})


export class GamesComponent {

  constructor( private http:HttpClient){};
  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
  };

  addGame(game:any){
    var gameInfo = new GameModel(game.name, game.genre, game.price, game.releaseDate);
    return this.http.post('https://localhost:7207/games', gameInfo).subscribe(data => {
      data = gameInfo;
  });
  }

  deleteGame(id:any){
    return this.http.delete('https://localhost:7207', id);
  }

  updateGame(game:any){
    return this.http.put('https://localhost:7207', game);
  }

  getGames(){
    return this.http.get('https://localhost:7207');
  }

  getGame(id:any){
    return this.http.get('https://localhost:7207', id);
  }

}

<div class="body">

<form #game = ngForm method="post" (ngSubmit)="addGame(game.value)">
    <label> Name </label>
    <input type = "text" name = "name" ngModel>
  <br>
    <label> Genre </label>
    <input type = "text" name = "genre" ngModel>
  <br>
    <label> Price  </label>
    <input type = "text" name = "price" ngModel>
  <br>
    <label> Release Date  </label>
    <input type="date" name = "releasedate" ngModel>
  <br>
  <button type="submit">
    Post
  </button>
</form>


<form #game = ngForm method="post" (ngSubmit)="deleteGame(game.value)">
  <label> ID </label>
  <input type = "text" name = "id" ngModel>
<button type="submit">
  Delete
</button>
</form>
</div>
