import { Component } from '@angular/core';
import {HttpClient} from '@angular/common/http';


@Component({
  selector: 'app-games',
  templateUrl: './games.component.html',
  styleUrls: ['./games.component.css']
})


export class GamesComponent {


  constructor( private http:HttpClient){};
  addGame(game:any){
    return this.http.post('https://localhost:7207', game);
  }

  deleteGame(id:any){
    return this.http.delete('https://localhost:7207', id);
  }

  updateGame(game:any){
    return this.http.put('https://localhost:7207', game);
  }

  getGames(){
    return this.http.get('https://localhost:7207');
  }

  getGame(id:any){
    return this.http.get('https://localhost:7207', id);
  }

}

<div class="body">

<form #game method="post" (ngSubmit)="addGame(game)">
    <label> Name </label>
    <input name = "name" ngModel>
  <br>
    <label> Genre </label>
    <input >
    <input >
  <br>
    <label> Price  </label>
    <input>
  <br>
    <label> Release Date  </label>
    <input type="date">
  <br>
  <button type="submit">
    Post
  </button>
</form>
</div>
